import React, { useState } from 'react'
// import { Navigate } from 'react-router-dom'
import { useNavigate } from 'react-router-dom'
import '../Style/form.css';

export const Form = (props) => {
    const [username, setUsername] = useState()
    const [password, setPassword] = useState()
    const [error, setError] = useState({username: false, password: false})
    const Navigate = useNavigate();
    const handleUsername = e => {
        if (e.target.value.match(/^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/) ) {
            setError(prev => { return {...prev, username: 'You have entered an invalid email address'}})
            setUsername(null)
        } else {
            setUsername(e.target.value)
            setError(prev => { return {...prev, username: false}})
        }
    }

    const handlePassword = e => {
        if (e.target.value.match(/(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{8,}/) ) {
            setError(prev => { return {...prev, password: 'Must contain at least one number and one uppercase and lowercase letter, and at least 8 or more characters'}})
            // setPassword(null)
        } else {
            setPassword(e.target.value)
            setError(prev => { return {...prev, password: false}})
        }
    }


    const handleSubmitForm = e => {
        if (Object.keys(error).some(item => item !== false)) alert('please enter valid user details')
        else {
                e.preventDefault()
                Navigate('/')
        }  
    }


  return (
    <form>
        {props.isLogin ? <div className='form-wrapper'>
           <label className="label" htmlFor='username'>Username</label>
            <input type="text" id ="username" value={username} placeholder="Enter username" onChange={handleUsername} title={error.username}/>
            <label className="label" htmlFor='password'>Password</label> <input id="password" value={password} type="text" placeholder="Enter password" onChange={handlePassword} title={error.password} />
           </div>: ''}
        <button className='btn btn-primary' onClick={handleSubmitForm}>{props.isLogin ? "Sign up" : "Login"}</button>
    </form>
)
}
