import React, { useState } from 'react'

export const Form = (props) => {
    const [username, setUsername] = useState()
    const [password, setPassword] = useState()
    const [error, setError] = useState({username: false, password: false})
    const handleUsername = e => {
        if (e.target.value.match(/^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/) ) {
            setError(prev => { return {...prev, username: 'You have entered an invalid email address'}})
            setUsername(null)
        } else {
            setUsername(e.target.value)
            setError(prev => { return {...prev, username: false}})
        }
    }

    const handlePassword = e => {
        if (e.target.value.match(/(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{8,}/) ) {
            setError(prev => { return {...prev, password: 'Must contain at least one number and one uppercase and lowercase letter, and at least 8 or more characters'}})
            setPassword(null)
        } else {
            setPassword(e.target.value)
            setError(prev => { return {...prev, password: false}})
        }
    }


  return (
    <div>
        {props.isSignUp ? <div>
            <label htmlFor='username'>Username</label>
            <input type="text" id ="username" value={username} placeholder="Enter username" onChange={handleUsername}/>
            <label htmlFor='password'>Password</label>
            <input id="password" value={password} type="text" placeholder="Enter password" onChange={handlePassword} title={error.password} />
        </div>: ''}
    </div>    
)
}
